<?php	include('conectar-bd.php');	$cod = $_GET['cod'];	$dato = mysql_query("SELECT * FROM observaciones WHERE soporte_cod = '$cod'");	$reg = mysql_fetch_array($dato);	$cant = mysql_num_rows($dato);	echo "<tr><th colspan='3'>Observaciones de la solicitud " . str_pad($cod, 6, "0",STR_PAD_LEFT) . "</th></tr>";	if($cant > 0){		echo "<input type='hidden' name='txtCod' value='" . $cod . "' />";		do{			echo "<tr><td>" . $reg['soporte_cod'] . "</td><td>" . $reg['descripcion'] . "</td><td><a href='editar-observacion.php?cod=" . $reg['id'] . "&descripcion=" . $reg['descripcion'] . "'><img src='images/edit.png' height='16px' width='16px' /></a></td></tr>";		}while($reg = mysql_fetch_array($dato));	}else{		echo "<tr><td>No hay observaciones para esta solicitud.</td></tr>";	}?>