<?php	include('conectar-bd.php');	$user = $_POST['txtUser'];	$pass = $_POST['txtPass'];	$pass = sha1($pass);	$dato = mysql_query("SELECT * FROM usuarios WHERE rut = '$user' AND password = '$pass' AND activo = '1'");	$cant = mysql_num_rows($dato);	$reg = mysql_fetch_array($dato);	$nombre = $reg['nombre'];	$apellido = $reg['apellido'];	$apellidoM = $reg['apellido_m'];	$user = $nombre . " " . $apellido . " " .$apellidoM;	$area = $reg['area'];	$tipo = $reg['tipo'];	if($cant>0){		session_start();		$_SESSION["user"] = $user;		$_SESSION["area"] = $area;		$_SESSION["tipo"] = $tipo;		$_SESSION['rut'] = $_POST['txtUser'];		header('location:index.php');	}else{		header('location:error.php');	}?>